
$(function(){
    var type=sessionStorage.getItem("applytype");
    // alert(type);
    switch(type){
        case "people": $(".applyDetail-type").html("个人");break;
        case "car": $(".applyDetail-type").html("汽车");break;
        case "house": $(".applyDetail-type").html("住房");break;
    }

    // $("#")

    $("#apply-sub-btn").on("click",function(){
        if($("#borrowmoney").val()=="" ||$("#interest").val()=="" ||$("#minbid").val()=="" ||$("#bouns").val()=="" ||$("#days").val()=="" ||$("#title").val()=="" ||$("#info").val()==""){
            alert("请将内容填写完整！");
            return false;
        }
        $.ajax({
            type: "get",
            url: "http://172.16.16.127:8282/borrow.php",
            data: {
                acc:localStorage.getItem("username"),
                borrowmoney:$("#borrowmoney").val(),
                interest:$("#interest").val(),
                borrowtime:$("#borrowtime").val(),
                repaytype:$("input[type=radio]:checked").val(),
                minbid:$("#minbid").val(),
                bouns:$("#bouns").val(),
                days:$("#days").val(),
                title:$("#title").val(),
                info:$("#info").val(),

            },
            // dataType: "dataType",
            success: function (response) {
                if(response=="ok"){
                    alert("发布成功！");
                    location.href="/#select";

                }else{
                    alert("发布失败！")
                }
                
            }
        });














    })
    


    







})