$(function(){

    $('[data-toggle="tooltip"]').tooltip();

    // 数据动态实现
    function numMove(obj){
        $(obj).each(function(){
            // console.log($(this))
            $(this).prop('Counter',0).animate({
                Counter: $(this).text()
            },{
                duration: 2500,
                easing: 'swing',
                step: function (now){
                    $(this).text(Math.ceil(now));
                }
            });
        });

      };

    //   setTimeout(function(){
    //     numMove(".count3");
    //   },1000)
      
      window.onscroll=function(){
          var sTop=document.documentElement.scrollTop;
          console.log(sTop);
          if(sTop==500){           
            numMove(".count3");
            // return false;
          }else if(sTop==700){
            numMove(".count4");
            // return false;

          }else if(sTop==900){
            numMove(".count5");
            // return false;
          }
          else if(sTop==1300){
            numMove(".count6");
            // return false;
          }
      }
      
      





});