$(function(){

    $("#recharge-btn").on("click",function(){
        if(!Number($("#input-money").val())){
            alert("请正确输入充值金额！！")
            return false;
        }
        $.ajax({
            type: "get",
            url: "http://172.16.16.127:8282/charge.php",
            data: {
                id:localStorage.getItem("id"),
                bankcode:$("#backcode").val(),
                chargemoney:$("#input-money").val()

            },
            success: function (response) {
                if(response=="ok"){
                    alert("充值成功！！");
                    location.href="#usercenter/myaccount";
                    $("#myaccount").trigger("click");
                }else{
                    alert("充值失败！")
                }
                
            }
        });

    })













    
})